// Extract the data array from the input
const promotions = $input.all()[0].json.data;

// Define the columns for Google Sheets
const columns = [
  'Promotion ID',
  'Type',
  'Advertiser ID',
  'Advertiser Name',
  'Joined',
  'Title',
  'Description',
  'Terms',
  'Start Date',
  'End Date',
  'Status',
  'URL',
  'Tracking URL',
  'Date Added',
  'Campaign',
  'Regions - All',
  'Regions - Countries'
];

// Format the data for Google Sheets
const formattedData = promotions.map(promo => {
  // Handle regions formatting
  let regionsAll = promo.regions.all ? 'Yes' : 'No';
  let regionsCountries = '';

  if (promo.regions.list && promo.regions.list.length > 0) {
    regionsCountries = promo.regions.list
      .map(region => `${region.name} (${region.countryCode})`)
      .join(', ');
  }

  // Format dates to be more readable
  const formatDate = (dateString) => {
    if (!dateString) return '';
    return new Date(dateString).toLocaleString('en-US', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
      hour12: false
    });
  };

  return {
    'Promotion ID': promo.promotionId,
    'Type': promo.type,
    'Advertiser ID': promo.advertiser.id,
    'Advertiser Name': promo.advertiser.name,
    'Joined': promo.advertiser.joined ? 'Yes' : 'No',
    'Title': promo.title,
    'Description': promo.description,
    'Terms': promo.terms,
    'Start Date': formatDate(promo.startDate),
    'End Date': formatDate(promo.endDate),
    'Status': promo.status,
    'URL': promo.url,
    'Tracking URL': promo.urlTracking,
    'Date Added': formatDate(promo.dateAdded),
    'Campaign': promo.campaign || '',
    'Regions - All': regionsAll,
    'Regions - Countries': regionsCountries
  };
});

// Return the formatted data
return formattedData;
