// Parse JIRA description table format to JSON
const input = $input.first();
const description = input.json.body.fields.description;

function parseJiraTable(text) {
  const result = {};

  // Find the table section
  const tableStart = text.indexOf('||*Fields*||*Values*||');
  if (tableStart === -1) {
    return { error: 'Table format not found' };
  }

  // Extract the table portion
  const tableSection = text.substring(tableStart);
  const lines = tableSection.split('\n'); // Fixed: use \n not \\n

  // Process each line that contains field-value pairs
  for (let line of lines) {
    line = line.trim();

    // Skip header and empty lines
    if (!line || line.includes('||*Fields*||*Values*||') || line.length < 3) {
      continue;
    }

    // Check if line starts and ends with |
    if (line.startsWith('|') && line.endsWith('|')) {
      // Remove leading and trailing pipes
      const content = line.slice(1, -1);
      const parts = content.split('|');

      if (parts.length >= 2) {
        let fieldName = parts[0].trim();
        let fieldValue = parts.slice(1).join('|').trim();

        // Clean field name
        fieldName = cleanFieldName(fieldName);

        // Process field value
        fieldValue = processFieldValue(fieldValue);

        result[fieldName] = fieldValue;
      }
    }
  }

  return result;
}

function cleanFieldName(fieldName) {
  // Remove special formatting and normalize
  return fieldName
    .replace(/[^a-zA-Z0-9\s\(\)\&\#\%\?\/]/g, '') // Remove special chars except common ones
    .trim()
    .toLowerCase()
    .replace(/\s+/g, '_') // Replace spaces with underscores
    .replace(/[\(\)\&\#\%\?\/]/g, '') // Remove remaining special chars
    .replace(/_+/g, '_') // Replace multiple underscores with single
    .replace(/^_|_$/g, ''); // Remove leading/trailing underscores
}

function processFieldValue(value) {
  if (!value || value.trim() === '') {
    return null;
  }

  // Handle JIRA link format [url|url]
  const linkMatch = value.match(/\[([^|]+)\|[^\]]+\]/);
  if (linkMatch) {
    return linkMatch[1]; // Extract the actual URL
  }

  // Clean up JIRA formatting
  value = value
    .replace(/^h1\.\s*/, '') // Remove h1. prefix
    .trim();

  return value;
}

// Parse the input
const parsedData = parseJiraTable(description);

// Return the result
return {
  json: {
    parsed_fields: parsedData,
    field_count: Object.keys(parsedData).length
  }
};

