try {
  // Get the input data
  const inputData = $('Webhook1').first().json.body.fields.customfield_10477;

  // Simple test first - just return the raw input
  if (!inputData || typeof inputData !== 'string') {
    return [{ json: { error: "Invalid input data", inputData: inputData } }];
  }

  // Function to parse a single table
  function parseTable(tableText) {
    try {
      const lines = tableText.trim().split('\n').filter(line => line.trim());

      if (lines.length < 3) return null;

      // Extract table title from first line
      const titleLine = lines[0];
      const titleMatch = titleLine.match(/\|([^|]+)\|/);
      const tableName = titleMatch ? titleMatch[1].trim() : 'Unknown Table';

      // Extract column headers from second line
      const headerLine = lines[1];
      const headers = headerLine
        .split('|')
        .map(h => h.replace(/\+/g, '').trim())
        .filter(h => h.length > 0);

      // Parse data rows (skip first two lines)
      const dataRows = [];
      for (let i = 2; i < lines.length; i++) {
        const line = lines[i];
        if (!line || !line.includes('|')) continue;

        const cells = line
          .split('|')
          .map(cell => cell.trim())
          .filter((cell, index, arr) => {
            return !(index === 0 && cell === '') && !(index === arr.length - 1 && cell === '');
          });

        if (cells.length > 0) {
          const rowData = {};
          headers.forEach((header, index) => {
            if (index < cells.length) {
              let value = cells[index];

              // Convert numeric values
              if (value && !isNaN(value) && value !== '') {
                value = parseFloat(value);
              }

              // Handle currency values
              if (typeof value === 'string' && value.startsWith('$')) {
                const numValue = parseFloat(value.substring(1));
                if (!isNaN(numValue)) {
                  value = numValue;
                }
              }

              rowData[header] = value || null;
            } else {
              rowData[header] = null;
            }
          });
          dataRows.push(rowData);
        }
      }

      // Filter data to only include specified fields
      const allowedFields = ['Goal For Members', 'Event Type', 'Revenue', 'Rewards'];
      const filteredData = dataRows.map(row => {
        const filteredRow = {};
        allowedFields.forEach(field => {
          if (row.hasOwnProperty(field)) {
            filteredRow[field] = row[field];
          }
        });
        return filteredRow;
      });

      return {
        tableName: tableName,
        data: filteredData
      };
    } catch (parseError) {
      return {
        error: "Error parsing table",
        errorMessage: parseError.message,
        tableText: tableText.substring(0, 200) // First 200 chars for debugging
      };
    }
  }

  // Split input into individual tables
  const sections = inputData.split(/\n\s*\n/);
  const tables = [];

  for (const section of sections) {
    if (section.trim() && section.includes('|')) {
      const parsedTable = parseTable(section);
      if (parsedTable) {
        tables.push(parsedTable);
      }
    }
  }

  // Ensure we always return an array
  if (tables.length === 0) {
    return [{ json: { message: "No tables found", sections: sections.length } }];
  }

  // Return each table as a separate item
  return tables.map(table => ({ json: table }));

} catch (error) {
  // Catch any unexpected errors
  return [{ 
    json: { 
      error: "Unexpected error", 
      message: error.message,
      stack: error.stack
    } 
  }];
}

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

