parameters.jsCode:
// Get all items from the input
const items = $input.all();

// Create a new merged object
let mergedOutput = {};

// Loop through each item and merge its output properties
items.forEach(item => {
  if (item.json && item.json.output) {
    // Merge all properties from the current item's output into the mergedOutput
    Object.assign(mergedOutput, item.json.output);
  }
});

// Return a single item with the merged output
return [
  {
    json: {
      output: mergedOutput
    }
  }
];

