function getGMT7Time() {
  const now = new Date();
  // Convert to GMT-7 (subtract 7 hours)
  const gmt7 = new Date(now.getTime() - (7 * 60 * 60 * 1000));
  const date = gmt7.toISOString().split('T')[0];
  const time = gmt7.toISOString().split('T')[1].substring(0, 5);
  return `${date} ${time}`;
}

