  groupedData[tableName].sort((a, b) => {
    // Handle cases where ordernum might be missing
    const orderA = a.ordernum !== undefined ? a.ordernum : Number.MAX_SAFE_INTEGER;
    const orderB = b.ordernum !== undefined ? b.ordernum : Number.MAX_SAFE_INTEGER;
    return orderA - orderB;
  });
}

// Create a table for each tableName
for (const [tableName, items] of Object.entries(groupedData)) {
  // Get all unique columns from items in this table
  const allColumns = new Set();
  items.forEach(item => {
    Object.keys(item).forEach(key => {
      if (columnMappings[key]) {
        allColumns.add(key);
      }
    });
  });

  // Convert to array and sort for consistent ordering
  const columns = Array.from(allColumns).sort();
  const columnCount = columns.length;

  // Add table header with tableName spanning all columns
  comment += `|| ${tableName} `;
  for (let i = 1; i < columnCount; i++) {
    comment += `|| `;
  }
  comment += `||\n`;

  // Create header row with mapped column names
  const headerRow = columns.map(col => columnMappings[col]).join('||');
  comment += `||${headerRow}||\n`;

  // Add data rows (already sorted by ordernum)
  for (const item of items) {
    const values = columns.map(col => {
      const value = item[col];

      // Handle different value types
      if (value === null || value === undefined) {
        return 'None';
      } else if (typeof value === 'number') {
        // Format numbers to 2 decimal places if they have decimals
        return Number.isInteger(value) ? value.toString() : value.toFixed(2);
      } else if (value instanceof Date) {
        // Format dates
        return value.toISOString().split('T')[0];
      } else {
        return value.toString();
      }
    });

    comment += `|${values.join('|')}|\n`;
  }

  // Add spacing between tables
  comment += '\n';
}

