  if (lines.length < 3) return; // Ensure there are enough lines to process (title, header, data)

  // Extract table name from the first line
  let tableName = `Table ${index + 1}`;
  const titleLine = lines[0];
  const titleMatch = titleLine.match(/\|\s*([^|]+)\s*\|/);
  if (titleMatch && titleMatch[1]) {
    tableName = titleMatch[1].trim();
  }

  // The header line is the second line
  const headerLine = lines[1];
  const headers = headerLine.split('||').map(h => h.trim()).filter(Boolean);

  // Map headers to their positions (accounting for empty first/last elements)
  const headerMap = {};
  headers.forEach((header, idx) => {
    headerMap[header.toLowerCase().replace(/\s+/g, '_')] = idx;
  });

  // Process data lines (starting from line 3)
  const dataLines = lines.slice(2);

  dataLines.forEach(line => {
    // Split by | and clean up
    const cols = line.split('|').map(c => c.trim());

    // Remove empty first and last elements if they exist
    if (cols[0] === '') cols.shift();
    if (cols[cols.length - 1] === '') cols.pop();

    // Skip if not enough columns
    if (cols.length < headers.length) return;

    // Extract goal name from Goal From Client column instead
    const goalName = cols[headerMap['goal_from_client']] || '';
    if (!goalName || goalName === 'None') return; // Skip entries without a valid goal name

    // Parse and normalize values
    const conversionRateRaw = cols[headerMap['conversion_rate']] || '';
    let conversionRate = null;
    if (conversionRateRaw && conversionRateRaw !== 'None') {
      if (conversionRateRaw.endsWith('%')) {
        conversionRate = parseFloat(conversionRateRaw.replace('%', '')) / 100;
      } else {
        conversionRate = parseFloat(conversionRateRaw);
      }
    }

    const estimatedConversionTimeRaw = cols[headerMap['estimated_conversion_time']] || '';
    let estimatedConversionTime = null;
    if (estimatedConversionTimeRaw && estimatedConversionTimeRaw !== 'None') {
      if (estimatedConversionTimeRaw.endsWith('%')) {
        estimatedConversionTime = parseFloat(estimatedConversionTimeRaw.replace('%', '')) / 100;
      } else {
        estimatedConversionTime = parseFloat(estimatedConversionTimeRaw);
      }
    }

    // Create a flat record with all fields
    const flatRecord = {
      orderNumber: orderNumber++, // Add order number and increment
      tableName: tableName,
      targetMargin: targetMargin,
      goalName: goalName,
      androidTokens: cols[headerMap['android_tokens']] !== 'None' ? cols[headerMap['android_tokens']] : null,
      conversionRate: conversionRate,
      estimatedConversionTime: estimatedConversionTime,
      eventType: cols[headerMap['event_type']] !== 'None' ? cols[headerMap['event_type']] : null,
      goalForMembers: cols[headerMap['goal_for_members']] !== 'None' ? cols[headerMap['goal_for_members']] : null,
      hsCreatedate: cols[headerMap['hs_createdate']] || null,
      hsLastmodifieddate: cols[headerMap['hs_lastmodifieddate']] || null,
      hsObjectId: cols[headerMap['hs_object_id']] || null,
      iosTokens: cols[headerMap['ios_tokens']] !== 'None' ? cols[headerMap['ios_tokens']] : null,
      maxTimeToComplete: cols[headerMap['maximum_time_to_complete']] !== 'None' ? parseFloat(cols[headerMap['maximum_time_to_complete']]) : null,
