  UNDER_5: 0.16,           // When purchaseAmount < $5
  BETWEEN_5_10: 0.09,      // When purchaseAmount $5-$10
  BETWEEN_10_15: 0.02,     // When purchaseAmount $10-$15
  OVER_15: 0.005           // When purchaseAmount $15+
};

// Price thresholds
const PRICE_THRESHOLDS = {
  TIER_1: 5,    // Under $5
  TIER_2: 10,   // $5 to $10
  TIER_3: 15    // $10 to $15
};

// Function to determine conversion rate based on purchase amount
function getConversionRate(purchaseAmount) {
  // Check if purchaseAmount is 0 or null/undefined
  if (purchaseAmount === 0 || purchaseAmount === null || purchaseAmount === undefined) {
    return CONVERSION_RATES.ZERO_OR_NULL;
  }

  // Determine rate based on price tiers
  if (purchaseAmount < PRICE_THRESHOLDS.TIER_1) {
    return CONVERSION_RATES.UNDER_5;
  } else if (purchaseAmount >= PRICE_THRESHOLDS.TIER_1 && purchaseAmount < PRICE_THRESHOLDS.TIER_2) {
    return CONVERSION_RATES.BETWEEN_5_10;
  } else if (purchaseAmount >= PRICE_THRESHOLDS.TIER_2 && purchaseAmount < PRICE_THRESHOLDS.TIER_3) {
    return CONVERSION_RATES.BETWEEN_10_15;
  } else {
    return CONVERSION_RATES.OVER_15;
  }
}

