  if (data.eventType === "Purchase" && data.conversionRate === null) {
    // Calculate and set the conversion rate
    data.conversionRate = getConversionRate(data.purchaseAmount);
  }

  // Add the item (modified or not) to the output
  processedItems.push({
    json: data
  });
}

return processedItems;
